INSERT INTO [dbo].[GALLERY_ITEM] ([Gallery_Item_Guid], [Icon_File_Name_Small], [Icon_File_Name_Large], [Configuration_Setup], [Description], [Configuration_Setup_Client], [Title], [Is_Visible], [CreationDate]) VALUES ('588b16e5-ae9a-435e-a6e3-441a7578d64a', N'CMMC.png', N'CMMC.png', N'{Model:{ModelName:"CMMC", Level:1}}', N'The Cybersecurity Maturity Model Certification (CMMC) 1.0 measures cybersecurity maturity with five levels and aligns a set of processes and practices with the type and sensitivity of information to be protected and the associated range of threats. The model consists of maturity processes and cybersecurity best practices from multiple cybersecurity standards, frameworks and other references, as well as inputs from the broader community. CMMC is broken into five different levels of Processes and Capabilities (which are further broken down into Practices).', NULL, N'Cybersecurity Maturity Model Certification 1.02', 1, '2022-09-20 15:26:46.847')
