INSERT INTO [dbo].[GALLERY_ITEM] ([Gallery_Item_Guid], [Icon_File_Name_Small], [Icon_File_Name_Large], [Configuration_Setup], [Description], [Configuration_Setup_Client], [Title], [Is_Visible], [CreationDate]) VALUES ('2962cbbd-740a-4600-9262-4c3cf80eaa5a', N'VADR.png', N'VADR.png', N'{Sets:["TSA2018"],SALLevel:"Low",QuestionMode:"Questions",Model:{ModelName:"VADR"}}', N'The VADR maturity model enables participants to perform assessments virtually. Virtual assessments include the same elements that make up a traditional VADR: Architecture Design Review, System Configuration and Log Review, as well as Network Traffic Analysis.', NULL, N'TSA Validated Architecture Design Reviews (VADR)', 1, '2022-09-20 15:26:46.847')
INSERT INTO [dbo].[GALLERY_ITEM] ([Gallery_Item_Guid], [Icon_File_Name_Small], [Icon_File_Name_Large], [Configuration_Setup], [Description], [Configuration_Setup_Client], [Title], [Is_Visible], [CreationDate]) VALUES ('3cc7b809-207c-423b-b1b3-4f04cd586999', N'', N'', N'{Sets:["Cnssi_Ics_Pit"],SALLevel:"Low",QuestionMode:"Questions"}', N'This Instruction serves as a companion document to NIST SP 800-53 for organizations that employ national security systems (NSS). It establishes the processes for categorizing NSS and the information they process and for appropriately selecting security controls for NSS from NIST SP 800-53. This Instruction applies to all components of NSS. For NSS, where differences between the NIST documentation and this Instruction occur, this Instruction is authoritative.', NULL, N'CNSSI No. 1253 Industrial Control System (ICS) Overlay', 1, '2022-09-20 15:26:46.847')
INSERT INTO [dbo].[GALLERY_ITEM] ([Gallery_Item_Guid], [Icon_File_Name_Small], [Icon_File_Name_Large], [Configuration_Setup], [Description], [Configuration_Setup_Client], [Title], [Is_Visible], [CreationDate]) VALUES ('7ba9f3e0-825f-481a-af3b-50b12638eec5', N'CSC_V8.png', N'CSC_V8.png', N'{Sets:["CSC_V8"],SALLevel:"Low",QuestionMode:"Questions"}', N'The CIS Controls are a prioritized set of safeguards to mitigate the most prevalent cyber-attacks against systems and networks.', NULL, N'CIS Controls Version 8', 1, '2022-09-20 15:26:46.847')
