INSERT INTO [dbo].[GALLERY_ITEM] ([Gallery_Item_Guid], [Icon_File_Name_Small], [Icon_File_Name_Large], [Configuration_Setup], [Description], [Configuration_Setup_Client], [Title], [Is_Visible], [CreationDate]) VALUES ('6a463c71-fe29-4189-bd7d-a0b1712b582c', N'CMMC.png', N'CMMC.png', N'{Model:{ModelName:"CMMC2", Level: 1}}', N'The new Cybersecurity Maturity Model Certification (CMMC) 2.0 streamlines the number of levels from 5 to 3. The most critical change is the removal of the third-party facilitated assessment certifications for Level 1 certification (and a subset of non-prioritized acquisitions assigned by DoD as Level 2) which now require only an <strong>annual self-assessment</strong> and <strong>accompanying contractor affirmation</strong> in the Supplier Performance Risk System (SPRS).', NULL, N'Draft CMMC 2.0 w/SPRS Scorecard', 1, '2022-09-20 15:26:46.847')
