INSERT INTO [dbo].[GALLERY_ITEM] ([Gallery_Item_Guid], [Icon_File_Name_Small], [Icon_File_Name_Large], [Configuration_Setup], [Description], [Configuration_Setup_Client], [Title], [Is_Visible], [CreationDate]) VALUES ('f7cf9b71-d8ef-4fd5-934f-ec94a0666b3c', NULL, NULL, N'{Sets:["C800_53_R4"],SALLevel:"Low",QuestionMode:"Questions"}', N'This standard provides a catalog of security and privacy controls for federal information systems and organizations and a process for selecting controls to protect organizational operations, organizational assets, individuals, other organizations, and the Nation from a diverse set of threats including hostile cyber attacks, natural disasters, structural failures, and human errors.', NULL, N'NIST Special Publication 800-53 Revision 4 (old)', 1, '2022-09-20 15:26:46.847')
