UPDATE [dbo].[MATURITY_QUESTIONS] SET [Supplemental_Info]=N' ', [Scope]=N'All public-facing web domains.', [Recommend_Action]=N'All public-facing web domains have a security.txt file that conforms to the recommendations in RFC 9116.', [Services]=N'<a href="https://securitytxt.org" target="_blank">https://securitytxt.org</a>' WHERE [Mat_Question_Id] = 9910
UPDATE [dbo].[MATURITY_QUESTIONS] SET [Supplemental_Info]=N' ', [Scope]=N'Organization-wide', [Recommend_Action]=N'Organizations have, maintain, update, and regularly drill IT and OT cybersecurity incident response plans for both common and organizationally-specific (e.g. by sector, locality, etc.) threat scenarios and TTPs. When conducted, tests or drills are as realistic in nature as feasible. IR plans are drilled at least annually, and are updated within an risk-informed time frame following the lessons learned portion of any exercise or drill.', [Risk_Addressed]=N'Inability to quickly and effectively contain, mitigate, and communicate about cybersecurity incidents.', [Services]=N'<a href="https://www.cisa.gov/cisa-tabletop-exercise-packages" target="_blank">Table Top Exercise Packages</a>,&#160;<a href="https://www.cisa.gov/critical-infrastructure-exercises" target="_blank">Critical Infrastructure Exercises</a>' WHERE [Mat_Question_Id] = 9911
